const products=[{id:"id-01",description:"Lorem ipsum dolor sit, amet consectetur adipisicing elit. Maxime, ullam amet quisquam, nulla saepe blanditiis alias, non incidunt repellat? non minima eius, vitae nulla dolorum",price:5e3,img:"img/01.jpg"},{id:"id-02",description:"Lorem ipsum dolor sit, amet consectetur adipisicing elit. Maxime, ullam amet quisquam, nulla saepe blanditiis alias, non incidunt repellat? non minima eius, vitae nulla dolorum",price:6e3,img:"img/01.jpg"},{id:"id-03",description:"Lorem ipsum dolor sit, amet consectetur adipisicing elit. Maxime, ullam amet quisquam, nulla saepe blanditiis alias, non incidunt repellat? non minima eius, vitae nulla dolorum",price:7e3,img:"img/01.jpg"},{id:"id-04",description:"Lorem ipsum dolor sit, amet consectetur adipisicing elit. Maxime, ullam amet quisquam, nulla saepe blanditiis alias, non incidunt repellat? non minima eius, vitae nulla dolorum",price:8e3,img:"img/01.jpg"}],basket={products:[],total:0};function generateCatalog(){const t=document.getElementById("products-container");products.forEach(e=>{const i=`\n      <div class="catalog__card">\n        <img src="${e.img}" alt="" class="catalog__card-img" />\n        <div class="catalog__card-id">${e.id}</div>\n        <div class="catalog__card-desc">\n          ${e.description}\n        </div>\n        <div class="catalog__card-cost">${e.price}$</div>\n        <button class="catalog__card-btn" data-product-id="${e.id}">PUSH TO BASKET</button>\n      </div>\n    `;t.innerHTML+=i});Array.from(document.querySelectorAll(".catalog__card-btn")).forEach(t=>t.addEventListener("click",t=>{const e=t.target.dataset.productId,i=products.find((function(t){return t.id===e}));basket.products.push(i),basket.total+=i.price,renderBasket()}))}function renderBasket(){document.getElementById("basket").innerHTML=`\n    <h2>Basket</h2>\n    ${0===basket.total?"\n      <p>Basket is empty</p>\n    ":'\n      <ul id="basket-products"></ul>\n      <p>Total: <span id="basket-total"></span></p>\n    '}\n  `;const t=document.getElementById("basket-products"),e=document.getElementById("basket-total");basket.products.forEach(e=>{t.innerHTML+=`\n      <li>\n        <p>${e.id}</p>\n        <img width="80" src="${e.img}" />\n        <p>${e.price}</p>\n        <button data-product-id="${e.id}" class="btn-basket-delete" onclick="deleteBasketProduct(this)">Delete</button>\n      </li>\n    `}),e.innerHTML=basket.total+"$"}function deleteBasketProduct(t){const e=t.dataset.productId;basket.products=basket.products.filter(t=>t.id!==e),basket.total=basket.products.reduce((t,e)=>t+e.price,0),renderBasket()}generateCatalog();